class LineCounter
    attr_reader :count

    def initialize(pdf, text_to_count, cell_width)
        @count = 0
        @cell_width = cell_width
        @pdf = pdf
        count_lines(text_to_count)
    end

    def count_lines(text)
        # we count each line the text_box want's to print
        # since we do not call any render method inside draw_text_callback, it won't get printed to the document
        rest = @pdf.text_box text, width: @cell_width, draw_text_callback: ->(_, _) { @count += 1 }
        count_lines(rest) unless rest == ""
    end
end

Prawn::Document.generate("test.pdf") do |pdf|
    # add some sample text to test cursors
    pdf.text "Hello World!"
    pdf.text "Another row"
    pdf.text "Yolo"

    # store current cursor position to start printing the real text at this position later
    current_cursor = pdf.cursor
    current_page = pdf.page_number

    # we could also get the cell width dynamically
    #
    # content = pdf.table(rows, ...)
    # cell_width = content.cells.at(0).width
    cell_width = 300

    # TODO: check if we could get the height of a line programmatically
    # maybe we should grab the first word (of first char), draw it with dry_run and get the height 🤔
    # PLEASE MIND: that this solution works with fixed row_heights only, different text-sizes will break this
    row_height = 13.8
    counter = LineCounter.new(pdf, text, cell_width)

    # build table data
    # PLEASE MIND: borders of cells that spans across pages will be rendered on first page only, so using vertical border won't work
    rows = [[
        { content: "", width: cell_width, height: row_height },
        { content: "B", rowspan: counter.count },
        { content: "C", rowspan: counter.count },
        { content: "D", rowspan: counter.count }
    ]]

    # not really sure why I have to decrement 1 from the count (since index starts at 1), but otherwise it prints an empty line at the end
    for i in 1..counter.count - 1 do
        rows << [{ content: "", width: cell_width, height: row_height }]
    end

    # print table
    pdf.table(rows, width: pdf.bounds.width, cell_style: { inline_format: true, overflow: :expand })

    # move cursor back to where the table begins
    pdf.go_to_page current_page
    pdf.move_cursor_to current_cursor

    # print actual text over table
    # now it looks like the text is inside the table
    span = pdf.span(cell_width, position: :left) do
        pdf.text text
    end
end